import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Typography, CircularProgress, Box, Chip } from '@mui/material';

const MovieDetail = () => {
    const { id } = useParams();
    const [movie, setMovie] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:5000/api/movies/${id}`)
            .then(res => res.json())
            .then(data => setMovie(data));
    }, [id]);

    if (!movie) return <CircularProgress />;

    return (
        <Container sx={{ py: 4 }}>
            <Typography variant="h4" gutterBottom>{movie.title}</Typography>
            {movie.poster && <img src={movie.poster} alt={movie.title} width="300" />}
            <Typography variant="body1" paragraph>{movie.plot}</Typography>
            <Box sx={{ mt: 2, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                {movie.genres?.map((g, i) => <Chip key={i} label={g} />)}
            </Box>
        </Container>
    );
};

export default MovieDetail;
